mapper.insert:

INSERT INTO DASHBOARDS (TITLE, HIDDEN, POSITION)
VALUES (
	#{title},
	#{hidden},
	#{position}
)


			INSERT INTO DASHBOARD_ATTRIBUTES (KEY, VALUE, DASHBOARD_ID)
			SELECT
			       DA.KEY, DA.VALUE, DA.DASHBOARD_ID FROM
			(SELECT
    			UNNEST(ARRAY[



				#{attribute.key}



			]) AS KEY,
			UNNEST(ARRAY[



				#{attribute.value}



				]) AS VALUE,
			#{dashboardId} AS DASHBOARD_ID) DA
			WHERE NOT EXISTS (
    			SELECT ID FROM DASHBOARDS WHERE ID = #{dashboardId} AND SYSTEM = TRUE
    		)

mapper.update:

            UPDATE
              DASHBOARD_ATTRIBUTES




                    KEY = #{key},




                    VALUE = #{value}




            WHERE
            	ID = #{id}
			AND NOT EXISTS (
				SELECT ID FROM DASHBOARDS WHERE ID = #{dashboardId} AND SYSTEM = TRUE
			)
